import React, { useEffect, useState } from "react";
import Loader from "../components/Loader";

export default function Main() {
  const [loader, setLoader] = useState(false);
  const img = document.getElementsByTagName("img");
  let loaded = 2;

  useEffect(() => {
    if (loader) {
      if (loaded === img.length) {
        console.log(img.length);
        console.log(loaded);
        const show = document.getElementById("sliderContainer");
        show.classList.remove("transform");
      }
    }
  }, [loaded]);
  /*   useEffect(() => {
    setTimeout(() => {

    }, 100); 
  }, []);*/
  setTimeout(() => {
    setLoader(true);
  }, 2000);
  let counter = 1;

  useEffect(() => {
    counter = 1;

    const interval = setInterval(() => {
      if (document.getElementById("radio" + counter)) {
        document.getElementById("radio" + counter).checked = true;

        counter++;
        if (counter > 3) {
          counter = 1;
        }
      } else {
        clearInterval(interval);
      }
    }, 5000);
    return () => clearInterval(interval);
  }, []);
  /*   const change = (x = 0) => {
    const interval = setInterval(() => {
      if (document.getElementById("radio" + counter)) {
        document.getElementById("radio" + counter).checked = true;
        counter++;
        if (counter > 3) {
          counter = 1;
        }
        console.log(interval);
      } else {
        clearInterval(interval);
      }
    }, 5000);
  }; */
  const setCounter = x => {
    counter = x;
  };
  const OnloadIncrease = () => {
    console.log(loaded);
    loaded += 1;
  };

  return (
    <div className="transform" id="sliderContainer">
      <div className=" slider">
        <div className="slides">
          <input type="radio" name="radio-btn" id="radio1" />
          <input type="radio" name="radio-btn" id="radio2" />
          <input type="radio" name="radio-btn" id="radio3" />

          <div className="first">
            <div className="slide__Logo">
              <img
                src="./img/MainDJBrand.png"
                alt="DjBrandLogo"
                onLoad={OnloadIncrease()}
              />
            </div>
          </div>
          <div className="slide second">
            <img src="./img/ja.jpg" alt="DJBrand" onLoad={OnloadIncrease()} />
          </div>
          <div className="slide third">
            <img src="./img/1.jpg" alt="wed" onLoad={OnloadIncrease()} />
          </div>
        </div>
        <div className="navigation-auto">
          <div className="auto-btn1" id="active1"></div>
          <div className="auto-btn2" id="active2"></div>
          <div className="auto-btn3" id="active3"></div>
        </div>

        <div className="navigation-manual">
          <label
            htmlFor="radio1"
            className="manual-btn"
            onClick={() => setCounter(1)}
          ></label>
          <label
            htmlFor="radio2"
            className="manual-btn"
            onClick={() => setCounter(2)}
          ></label>
          <label
            htmlFor="radio3"
            className="manual-btn"
            onClick={() => setCounter(3)}
          ></label>
        </div>
      </div>
    </div>
  );
}
