import { BrowserRouter, Link, Route } from "react-router-dom";
import {
  faCompactDisc,
  faUser,
  faMicrophoneAlt,
  faCommentDots,
} from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import Main from "./screens/Main.js";
import AboutMe from "./screens/AboutMe.js";
import Contact from "./screens/Contakt.js";
import Offert from "./screens/Offer.js";
import { createElement, useEffect, useRef } from "react";

const slides = [
  {
    title: "Machu Picchu",
    subtitle: "Peru",
    description: "Adventure is never far away",
    image:
      "https://images.unsplash.com/photo-1571771019784-3ff35f4f4277?ixlib=rb-1.2.1&q=80&fm=jpg&crop=entropy&cs=tinysrgb&w=800&fit=max&ixid=eyJhcHBfaWQiOjE0NTg5fQ",
  },

  {
    title: "Chamonix",
    subtitle: "France",
    description: "Let your dreams come true",
    image:
      "https://images.unsplash.com/photo-1581836499506-4a660b39478a?ixlib=rb-1.2.1&q=80&fm=jpg&crop=entropy&cs=tinysrgb&w=800&fit=max&ixid=eyJhcHBfaWQiOjE0NTg5fQ",
  },

  {
    title: "Mimisa Rocks",
    subtitle: "Australia",
    description: "A piece of heaven",
    image:
      "https://images.unsplash.com/photo-1566522650166-bd8b3e3a2b4b?ixlib=rb-1.2.1&q=80&fm=jpg&crop=entropy&cs=tinysrgb&w=800&fit=max&ixid=eyJhcHBfaWQiOjE0NTg5fQ",
  },

  {
    title: "Four",
    subtitle: "Australia",
    description: "A piece of heaven",
    image:
      "https://images.unsplash.com/flagged/photo-1564918031455-72f4e35ba7a6?ixlib=rb-1.2.1&q=80&fm=jpg&crop=entropy&cs=tinysrgb&w=800&fit=max&ixid=eyJhcHBfaWQiOjE0NTg5fQ",
  },

  {
    title: "Five",
    subtitle: "Australia",
    description: "A piece of heaven",
    image:
      "https://images.unsplash.com/photo-1579130781921-76e18892b57b?ixlib=rb-1.2.1&q=80&fm=jpg&crop=entropy&cs=tinysrgb&w=800&fit=max&ixid=eyJhcHBfaWQiOjE0NTg5fQ",
  },
];

function useTilt(active) {
  const ref = useRef(null);

  useEffect(() => {
    if (!ref.current || !active) {
      return;
    }

    const state = {
      rect: undefined,
      mouseX: undefined,
      mouseY: undefined,
    };

    let el = ref.current;

    const handleMouseMove = e => {
      if (!el) {
        return;
      }
      if (!state.rect) {
        state.rect = el.getBoundingClientRect();
      }
      state.mouseX = e.clientX;
      state.mouseY = e.clientY;
      const px = (state.mouseX - state.rect.left) / state.rect.width;
      const py = (state.mouseY - state.rect.top) / state.rect.height;

      el.style.setProperty("--px", px);
      el.style.setProperty("--py", py);
    };

    el.addEventListener("mousemove", handleMouseMove);

    return () => {
      el.removeEventListener("mousemove", handleMouseMove);
    };
  }, [active]);

  return ref;
}

const initialState = {
  slideIndex: 0,
};

const slidesReducer = (state, event) => {
  if (event.type === "NEXT") {
    return {
      ...state,
      slideIndex: (state.slideIndex + 1) % slides.length,
    };
  }
  if (event.type === "PREV") {
    return {
      ...state,
      slideIndex:
        state.slideIndex === 0 ? slides.length - 1 : state.slideIndex - 1,
    };
  }
};

function Slide({ slide, offset }) {
  const active = offset === 0 ? true : null;
  const ref = useTilt(active);

  return createElement(
    "div",
    {
      ref: ref,
      className: "slide",
      "data-active": active,
      style: {
        "--offset": offset,
        "--dir": offset === 0 ? 0 : offset > 0 ? 1 : -1,
      },
    },

    createElement("div", {
      className: "slideBackground",
      style: {
        backgroundImage: `url('${slide.image}')`,
      },
    }),

    createElement(
      "div",
      {
        className: "slideContent",
        style: {
          backgroundImage: `url('${slide.image}')`,
        },
      },

      createElement(
        "div",
        { className: "slideContentInner" },
        createElement("h2", { className: "slideTitle" }, slide.title),
        createElement("h3", { className: "slideSubtitle" }, slide.subtitle),
        createElement("p", { className: "slideDescription" }, slide.description)
      )
    )
  );
}

/* function App() {
  const [state, dispatch] = useReducer(slidesReducer, initialState);

  return (
    createElement("div", { className: "slides" }, 
    createElement("button", { onClick: () => dispatch({ type: "PREV" }) }, "\u2039"),

    [...slides, ...slides, ...slides].map((slide, i) => {
      let offset = slides.length + (state.slideIndex - i);
      return createElement(Slide, { slide: slide, offset: offset, key: i });
    }), 
    createElement("button", { onClick: () => dispatch({ type: "NEXT" }) }, "\u203A")));


}

 */

function App() {
  return (
    <BrowserRouter>
      <div className="App">
        <div className="header">
          <div className="header__logo">
            <Link to="/">
              <img src="./img/DJBrandMenu.png" alt="logo DJ Brand" />
            </Link>
          </div>
          <div className="header__nav">
            <Link className="header__nav__box" to="/">
              <div className="header__nav__box__text">
                Start
                <FontAwesomeIcon icon={faCompactDisc} className="icon" />
              </div>
            </Link>
            <Link className="header__nav__box" to="/o_mnie">
              <div className="header__nav__box__text">
                O mnie
                <FontAwesomeIcon icon={faUser} className="icon" />
              </div>
            </Link>
            <Link className="header__nav__box" to="/oferta">
              <div className="header__nav__box__text">
                Oferta
                <FontAwesomeIcon icon={faMicrophoneAlt} className="icon" />
              </div>
            </Link>
            <Link className="header__nav__box" to="/kontakt">
              <div className="header__nav__box__text">
                Kontakt
                <FontAwesomeIcon icon={faCommentDots} className="icon" />
              </div>
            </Link>
          </div>
        </div>
        <main>
          <Route path="/" component={Main} exact />
          <Route path="/o_mnie" component={AboutMe} exact />
          <Route path="/oferta" component={Offert} exact />
          <Route path="/kontakt" component={Contact} exact />
        </main>
        <div className="footer">
          <div className="footer__Logo">
            <img src="./img/only_dj.png" alt="logo DJ " />{" "}
            <span>
              Dj Brand - Profesjonalny Dj, konferansjer, wodzirej na wesele,
              studniówkę, imprezy firmowe na Śląsku i okolicach.
            </span>
          </div>
          <div className="footer__socials">
            Złap mnie na :
            <div>
              <a href="https://www.facebook.com/DJBrand.Mateusz.Swiatczak">
                <i className="fab fa-facebook-square"></i>
              </a>
              <a href="https://www.instagram.com/saint_experience/">
                <i className="fab fa-instagram-square"></i>
              </a>
            </div>
          </div>
          <div className="footer__contactText">
            <div>
              <div className="footer__contactText__text">
                <i className="fas fa-mobile-alt"></i> 513-564-720
              </div>
              <div className="footer__contactText__text">
                <i className="far fa-envelope"></i> djmateuszswiatczak@gmail.com
              </div>
            </div>
          </div>
          <Link className="footer__kontaktLink" to="/kontakt">
            <div className="footer__kontaktLinkDirect">Kontakt</div>
          </Link>
        </div>
      </div>
    </BrowserRouter>
  );
}

export default App;
