import React, { useEffect } from "react";

export default function Main() {
  const show = document.getElementsByClassName("transform");
  /* show.classList.remove("transform"); */
  console.log(show);

  let counter = 1;
  useEffect(() => {
    changer();
  }, [counter]);
  const changer = () => {
    setTimeout(() => {
      document.getElementById("radio" + counter).checked = true;
      counter++;
      if (counter > 3) {
        counter = 1;
      }
      changer();
    }, 7000);
  };
  return (
    <div className="transform" id="sliderContainer">
      <div className=" slider">
        <div className="slides">
          <input type="radio" name="radio-btn" id="radio1" />
          <input type="radio" name="radio-btn" id="radio2" />
          <input type="radio" name="radio-btn" id="radio3" />

          <div className="first">
            <div className="slide__Logo">
              <img src="./img/MainDJBrand.png" alt="DjBrandLogo" />
            </div>
          </div>
          <div className="slide second">
            <img src="./img/ja.jpg" alt="wed" />
          </div>
          <div className="slide third">
            <img src="./img/1.jpg" alt="wed" />
          </div>
        </div>
        <div className="navigation-auto">
          <div className="auto-btn1"></div>
          <div className="auto-btn2"></div>
          <div className="auto-btn3"></div>
        </div>

        <div className="navigation-manual">
          <label htmlFor="radio1" className="manual-btn"></label>
          <label htmlFor="radio2" className="manual-btn"></label>
          <label htmlFor="radio3" className="manual-btn"></label>
        </div>
      </div>
    </div>
  );
}
