import React, { createElement, useEffect, useReducer, useRef } from "react";

export default function Offert() {
  /* useEffect(() => {
    const show = document.getElementById("aboutme");
    show.classList.remove("transform");
  }, []); */
  const slides = [
    {
      title: "Machu Picchu",
      subtitle: "Peru",
      description: "Adventure is never far away",
      image:
        "https://images.unsplash.com/photo-1571771019784-3ff35f4f4277?ixlib=rb-1.2.1&q=80&fm=jpg&crop=entropy&cs=tinysrgb&w=800&fit=max&ixid=eyJhcHBfaWQiOjE0NTg5fQ",
    },

    {
      title: "Chamonix",
      subtitle: "France",
      description: "Let your dreams come true",
      image:
        "https://images.unsplash.com/photo-1581836499506-4a660b39478a?ixlib=rb-1.2.1&q=80&fm=jpg&crop=entropy&cs=tinysrgb&w=800&fit=max&ixid=eyJhcHBfaWQiOjE0NTg5fQ",
    },

    {
      title: "Mimisa Rocks",
      subtitle: "Australia",
      description: "A piece of heaven",
      image:
        "https://images.unsplash.com/photo-1566522650166-bd8b3e3a2b4b?ixlib=rb-1.2.1&q=80&fm=jpg&crop=entropy&cs=tinysrgb&w=800&fit=max&ixid=eyJhcHBfaWQiOjE0NTg5fQ",
    },
  ];

  function useTilt(active) {
    const ref = useRef(null);

    useEffect(() => {
      if (!ref.current || !active) {
        return;
      }

      const state = {
        rect: undefined,
        mouseX: undefined,
        mouseY: undefined,
      };

      let el = ref.current;

      const handleMouseMove = e => {
        if (!el) {
          return;
        }
        if (!state.rect) {
          state.rect = el.getBoundingClientRect();
        }
        state.mouseX = e.clientX;
        state.mouseY = e.clientY;
        const px = (state.mouseX - state.rect.left) / state.rect.width;
        const py = (state.mouseY - state.rect.top) / state.rect.height;

        el.style.setProperty("--px", px);
        el.style.setProperty("--py", py);
      };

      el.addEventListener("mousemove", handleMouseMove);

      return () => {
        el.removeEventListener("mousemove", handleMouseMove);
      };
    }, [active]);

    return ref;
  }

  const initialState = {
    slideIndex: 0,
  };

  const slidesReducer = (state, event) => {
    if (event.type === "NEXT") {
      return {
        ...state,
        slideIndex: (state.slideIndex + 1) % slides.length,
      };
    }
    if (event.type === "PREV") {
      return {
        ...state,
        slideIndex:
          state.slideIndex === 0 ? slides.length - 1 : state.slideIndex - 1,
      };
    }
  };

  function Slide({ slide, offset }) {
    const active = offset === 0 ? true : null;
    const ref = useTilt(active);

    return createElement(
      "div",
      {
        ref: ref,
        className: "slide",
        "data-active": active,
        style: {
          "--offset": offset,
          "--dir": offset === 0 ? 0 : offset > 0 ? 1 : -1,
        },
      },

      createElement("div", {
        className: "slideBackground",
        style: {
          backgroundImage: `url('${slide.image}')`,
        },
      }),

      createElement(
        "div",
        {
          className: "slideContent",
          style: {
            backgroundImage: `url('${slide.image}')`,
          },
        },

        createElement(
          "div",
          { className: "slideContentInner" },
          createElement("h2", { className: "slideTitle" }, slide.title),
          createElement("h3", { className: "slideSubtitle" }, slide.subtitle),
          createElement(
            "p",
            { className: "slideDescription" },
            slide.description
          )
        )
      )
    );
  }

  const [state, dispatch] = useReducer(slidesReducer, initialState);

  return createElement(
    "div",
    { className: "slides" },
    createElement(
      "button",
      { onClick: () => dispatch({ type: "PREV" }) },
      "\u2039"
    ),

    [...slides, ...slides, ...slides].map((slide, i) => {
      let offset = slides.length + (state.slideIndex - i);
      return createElement(Slide, { slide: slide, offset: offset, key: i });
    }),
    createElement(
      "button",
      { onClick: () => dispatch({ type: "NEXT" }) },
      "\u203A"
    )
  );
}
